import pool from '../../config/config';

export default {
  /**
     * DB Query
     * @param {object} req
     * @param {object} res
     * @returns {object} object
     */
  query(text, params) {
    return new Promise((resolve, reject) => {
      pool.query(text, params)
        .then((res) => {
          resolve(res);
        })
        .catch((err) => {
          reject(err);
        });
    });
  },
};

