  deleteLoan(req, res) {
    const loan = loansModel.findALoan(req.params.id);
    if (!loan) {
      return res.status(404).send({ message: 'loan not found' });
    }
    const ref = loansModel.deleteLoan(req.params.id);
    return res.status(204).send(ref);
  },
};

export default Loan;

